SELECT COUNT(*) FROM census WHERE age <= 41.0 AND workclass = 'Private' AND education = '1st-4th' AND education_num <= 13.0 AND marital_status = 'Separated' AND occupation = 'Other-service' AND relationship = 'Other-relative' AND race = 'Asian-Pac-Islander' AND sex = 'Male' AND capital_gain <= 4720.0 AND capital_loss <= 2155.0 AND hours_per_week <= 60.0 AND native_country = 'Portugal';
