SELECT COUNT(*) FROM census WHERE age <= 57.0 AND workclass = 'Local-gov' AND education = '1st-4th' AND education_num <= 12.0 AND occupation = 'Transport-moving' AND relationship = 'Unmarried' AND race = 'Amer-Indian-Eskimo' AND capital_gain <= 35973.0 AND capital_loss <= 1835.0 AND hours_per_week <= 87.0;
