SELECT COUNT(*) FROM census WHERE workclass = 'Local-gov' AND education = '1st-4th' AND education_num <= 12.0 AND marital_status = 'Separated' AND occupation = 'Farming-fishing' AND relationship = 'Other-relative' AND race = 'White' AND sex = 'Male' AND hours_per_week <= 60.0 AND native_country = 'Hong';
